import osimport shutildef sort_files(path):    name = "pics_of_unmasked_add2_" # 开始的名称    fileType = ".jpg" # 类型    startNumber = 1; # 起始计数    count = 0;        folder_list = os.listdir(path)    for folder in folder_list:        sub_dir = os.path.join(path, folder)        if not os.path.isdir(sub_dir):            continue        files = os.listdir(sub_dir)        for file in files:            old_dir = os.path.join(sub_dir, file)            if os.path.isdir(old_dir):                continue            new_dir = os.path.join(sub_dir, name+str(count+startNumber)+fileType)            os.rename(old_dir, new_dir)            shutil.copy(new_dir, r"dir") # 要移动去的文件夹地址            count += 1path1 = r"dir"sort_files(path1)            